const initialState = {
    todos: [
        {
          id: uuidv4(),
          title: 'Learn react',
          completed: false
        },
        {
          id: uuidv4(),
          title: 'Dinner with Ania',
          completed: false
        },{
          id: uuidv4(),
          title: 'Read book with Eric',
          completed: false
        }
      ]}

  const todosReducer = (state = initialState, action) => {

    switch (action.type) {
        case 'newTodo':
            return {todos: [...this.state.todos, action.payload]}
        case 'delTodo':
            return {todos: [...this.state.todos.filter(todo => todo.id !== payload)]}
        case 'toogleTodo':
            const newState = {...state}
            const index = this.state.todos.findIndex(todo => todo.id === payload)
            if (index > -1){
                newState.todos[index].completed = !newState.todos[index].completed
            }
            return newState
        default:
            return state
    }
 }
  export default todosReducer
